function divarSortDateToTehranPretty(sort_date) {
    if (!sort_date || sort_date === 'Not Found') return '';
    const date = new Date(sort_date);
    return date.toLocaleString('fa-IR', {
        timeZone: 'Asia/Tehran',
        hour: '2-digit',
        minute: '2-digit',
        day: 'numeric',
