const results = [];
const htmlPages = $json.data; // This is an array of {data: "<html>...</html>"}

for (const htmlObj of htmlPages) {
    const html = htmlObj.data;

    const scriptTagRegex = /window\.__PRELOADED_STATE__\s*=\s*(\{[\s\S]*?\})\s*;/;
    const match = html.match(scriptTagRegex);

    if (!match || !match[1]) {
        console.error("Could not find __PRELOADED_STATE__ in the HTML.");
        continue;
    }

    let preloadedState;
    try {
        preloadedState = JSON.parse(match[1]);
    } catch (e) {
        console.error("Failed to parse __PRELOADED_STATE__ JSON.", e);
        continue;
    }

    const widgets = preloadedState?.nb?.listWidgets;
    if (!Array.isArray(widgets)) {
        console.error("listWidgets array not found in the expected location.");
        continue;
    }

    for (const widget of widgets) {
        if (widget.type !== 'WIDGET' || widget.data.widgetType !== 'POST_ROW') {
            continue;
        }

        const postData = widget.data;
        const postLinkPath = postData.action?.props?.to?.pathname || '';

        // Get the date string from Divar
        const sortDateString = postData.actionLog?.server_side_info?.info?.sort_date || null;

        // Convert the date string to an epoch timestamp number
        const epochTimestamp = sortDateString ? new Date(sortDateString).getTime() : null;

