// Function to convert Persian/Arabic digits to English digits
function toEnglishDigits(str) {
  if (!str) return '';
  return str.replace(/[Û°-Û¹]/g, d => 'Û°Û±Û²Û³Û´ÛµÛ¶Û·Û¸Û¹'.indexOf(d));
