// Function to clean and parse currency strings
function parseCurrency(str) {
  if (typeof str !== 'string' || str.trim() === '') return 0;
  const cleanedStr = toEnglishDigits(str).replace(/[^0-9]/g, '');
  return parseInt(cleanedStr, 10) || 0;
}

// Function to convert numbers to Persian words
function numberToPersianWords(num) {
  if (num === 0) return 'ØµÙØ±';
  const parts = [];
  const yekan = ["", "ÛŒÚ©", "Ø¯Ùˆ", "Ø³Ù‡", "Ú†Ù‡Ø§Ø±", "Ù¾Ù†Ø¬", "Ø´Ø´", "Ù‡ÙØª", "Ù‡Ø´Øª", "Ù†Ù‡"];
  const dahgan = ["", "", "Ø¨ÛŒØ³Øª", "Ø³ÛŒ", "Ú†Ù‡Ù„", "Ù¾Ù†Ø¬Ø§Ù‡", "Ø´ØµØª", "Ù‡ÙØªØ§Ø¯", "Ù‡Ø´ØªØ§Ø¯", "Ù†ÙˆØ¯"];
  const sadgan = ["", "ÛŒÚ©ØµØ¯", "Ø¯ÙˆÛŒØ³Øª", "Ø³ÛŒØµØ¯", "Ú†Ù‡Ø§Ø±ØµØ¯", "Ù¾Ø§Ù†ØµØ¯", "Ø´Ø´ØµØ¯", "Ù‡ÙØªØµØ¯", "Ù‡Ø´ØªØµØ¯", "Ù†Ù‡ØµØ¯"];
  const dah = ["Ø¯Ù‡", "ÛŒØ§Ø²Ø¯Ù‡", "Ø¯ÙˆØ§Ø²Ø¯Ù‡", "Ø³ÛŒØ²Ø¯Ù‡", "Ú†Ù‡Ø§Ø±Ø¯Ù‡", "Ù¾Ø§Ù†Ø²Ø¯Ù‡", "Ø´Ø§Ù†Ø²Ø¯Ù‡", "Ù‡ÙØ¯Ù‡", "Ù‡Ø¬Ø¯Ù‡", "Ù†ÙˆØ²Ø¯Ù‡"];

  function threeDigitToWords(n) {
    let result = [];
    const sad = Math.floor(n / 100);
    const dahSad = n % 100;
    const yek = n % 10;

    if (sad > 0) result.push(sadgan[sad]);

    if (dahSad >= 10 && dahSad < 20) {
      result.push(dah[dahSad - 10]);
    } else {
      const dahVal = Math.floor(dahSad / 10);
      if (dahVal > 0) result.push(dahgan[dahVal]);
      if (yek > 0) result.push(yekan[yek]);
    }
    return result.join(' Ùˆ ');
  }

  const scales = ["", "Ù‡Ø²Ø§Ø±", "Ù…ÛŒÙ„ÛŒÙˆÙ†", "Ù…ÛŒÙ„ÛŒØ§Ø±Ø¯", "ØªØ±ÛŒÙ„ÛŒÙˆÙ†"];
  let scaleIndex = 0;

  while (num > 0) {
    const chunk = num % 1000;
    if (chunk > 0) {
      parts.unshift(threeDigitToWords(chunk) + (scales[scaleIndex] ? ' ' + scales[scaleIndex] : ''));
    }
    num = Math.floor(num / 1000);
    scaleIndex++;
  }

  return parts.join(' Ùˆ ');
}

// Array of Persian month names
const persianMonths = ['ÙØ±ÙˆØ±Ø¯ÛŒÙ†', 'Ø§Ø±Ø¯ÛŒØ¨Ù‡Ø´Øª', 'Ø®Ø±Ø¯Ø§Ø¯', 'ØªÛŒØ±', 'Ù…Ø±Ø¯Ø§Ø¯', 'Ø´Ù‡Ø±ÛŒÙˆØ±', 'Ù…Ù‡Ø±', 'Ø¢Ø¨Ø§Ù†', 'Ø¢Ø°Ø±', 'Ø¯ÛŒ', 'Ø¨Ù‡Ù…Ù†', 'Ø§Ø³ÙÙ†Ø¯'];
const item = $json;

// 1. Calculate Total Mortgage (Numerical)
